components.customConditional:
let repeat = false;
data.dynamicData.enrolmentOfficers.forEach(enrolment => {
    if (enrolment.name !== "") {
        data.dynamicData.eligibilityOfficers.forEach(eligibility => {
            if (enrolment.name === eligibility.name) {
                repeat = true;
            }
        });
    }
});
show = (data.isBCPrivate && repeat);
