components.calculateValue:
const sabcCode = data.sabcCodes[data.cipCode.substring(0, 2)];
const levelOfStudyCode =  data.levelOfStudyCodes[data.credentialType];
value = (sabcCode && levelOfStudyCode) ? data.fieldOfStudyCodes[sabcCode + levelOfStudyCode] : 20;

validate.custom:
const shouldSelectOneOfTheAboveOrAnotherOption =
  (data.entranceRequirements.minHighSchool ||
    data.entranceRequirements.hasMinimumAge ||
    data.entranceRequirements.requirementsByInstitution ||
    data.entranceRequirements.requirementsByBCITA) &&
  data.entranceRequirements.noneOfTheAboveEntranceRequirements;

valid = shouldSelectOneOfTheAboveOrAnotherOption
  ? "Please select at least one of the entrance requirements or 'None of the above'."
  : true;

components.calculateValue:
if (
  data.hasJointDesignatedInstitution === "no" ||
  data.eslEligibility === "20OrMore" ||
  data.entranceRequirements.noneOfTheAboveEntranceRequirements === true ||
  (data.deliveredOnlineAlsoOnsite === "no" &&
    data.sameOnlineCreditsEarned === "no" &&
    data.earnAcademicCreditsOtherInstitution === "no") ||
  (data.courseLoadCalculation === "hours" &&
    data.minHoursWeek === "no" &&
    data.isAviationProgram === "no") ||
  (data.courseLoadCalculation === "hours" &&
    data.minHoursWeek === "no" &&
    data.isAviationProgram === "yes" &&
    data.minHoursWeekAvi === "no") ||
  (data.hasWILComponent === "yes" && data.isWILApproved === "no") ||
  (data.hasWILComponent === "yes" &&
    data.isWILApproved === "yes" &&
    data.wilProgramEligibility === "no") ||
  (data.hasTravel === "yes" && data.travelProgramEligibility === "no") ||
  (data.hasIntlExchange === "yes" &&
    data.intlExchangeProgramEligibility === "no")
) {
  value = "Pending";
} else {
  value = "Approved";
}
