components.calculateValue:
const mismatchValue = !(
  data.hasOfferingWILComponent === "no" || data.hasWILComponent === "yes"
);

value = !!(data.hasOfferingWILComponent && mismatchValue);

// To calculate the time difference of two dates
const differenceInTime =
  new Date(data.studyEndDate).getTime() -
  new Date(data.studyStartDate).getTime();

// To calculate the no. of days between two dates
const differenceInDays = differenceInTime / (1000 * 3600 * 24);

const mismatchValue = !(differenceInDays >= 42 && differenceInDays <= 365);
value = !!(data.studyStartDate && data.studyEndDate && mismatchValue);

var days = Date.parse(data.studyEndDate) - Date.parse(data.studyStartDate);
var studyDays = Math.round(days / (1000 * 60 * 60 * 24));
var studyBreakDays = 0;
var errorMessage;

if (data.studyBreaks && data.studyBreaks.length > 0) {
    for (let i = 0; i < data.studyBreaks.length; i++) {
      var days =
        Date.parse(data.studyBreaks[i].breakEndDate) -
        Date.parse(data.studyBreaks[i].breakStartDate);
      studyBreakDays =
        studyBreakDays + Math.round(days / (1000 * 60 * 60 * 24));
    }
}
  
var ifBreakExceedsMaxPercentage = studyBreakDays > studyDays * 0.1;
var ifBreakExceedMaxDays = studyBreakDays > 21;

if (ifBreakExceedsMaxPercentage) {
  errorMessage = "Your break(s) can't exceed 10% of study dates.";
}

if (ifBreakExceedMaxDays) {
  errorMessage = "Your break(s) can't exceed 21 days.";
}

if(ifBreakExceedsMaxPercentage && ifBreakExceedMaxDays){
  errorMessage = "Your break(s) can't exceed 10% of study dates and exceed 21 days.";
}

data.invalidStudyBreakMessage = errorMessage;
value = !!errorMessage;
