components.customConditional:
/**
 * Allow the online delivery option inputs only for
 * BC Public and BC Private Institutions
 * when the delivery type is either online or blended.
 */
show = ['online','blended'].includes(data.offeringDelivered) && (data.isBCPublic || data.isBCPrivate);

validate.custom:
// Validation logic.
if (input) {
  if (data.studyStartDate && data.studyEndDate) {
    if (input >= data.studyStartDate && input <= data.studyEndDate) {
      // study break overlapping detecting logic.
      if (data.studyBreaksOverlappingDetection) {
        valid = data.studyBreaksOverlappingDetection;
      } else {
        valid = true;
      }
    } else {
      valid =
        "Break Start date should be in between study start date and study end date.";
    }
  } else {
    valid = "Study start date or study end date can't be empty.";
  }
}

