}

components.calculateValue:
value = 0;
if (row.breakStartDate && row.breakEndDate) {
  const diffDays = moment(row.breakEndDate, "YYYY-MM-DD").diff(
    moment(row.breakStartDate, "YYYY-MM-DD"),
    "days"
  ) + 1 ;
  value = Math.max(diffDays, 0);
}

// Maximum of 21 days per break are eligible.
// Any days over 21 consecutive days must be unfunded
value = row.breakDays && (row.breakDays > 21) ? 21 : row.breakDays;

value = false;
const dateTimes = data.studyBreaks;
dateTimes?.forEach((time, i) => {
  let st1 = time.breakStartDate;
  let et1 = time.breakEndDate;

  dateTimes?.forEach((time2, j) => {
    if (i != j) {
      let st2 = time2.breakStartDate;
      let et2 = time2.breakEndDate;

      if (
        (st1 >= st2 && st1 <= et2) ||
        (et1 >= st2 && et1 <= et2) ||
        (st2 >= st1 && st2 <= et1) ||
        (et2 >= st1 && et2 <= et1)
      ) {
        value = "Overlapping study breaks detected.";
      }
    }
  });
});
