validate.custom:
var isEndDateValid = (row.breakStartDate && row.breakStartDate<row.breakEndDate);
if(isEndDateValid){
    var days = Date.parse(data.studyEndDate) - Date.parse(data.studyStartDate);
    var studyDays = Math.round((days/(1000*60*60*24)));
    var studyBreakDays = 0;
    var errorMessage;
    if(data.studyBreaks && data.studyBreaks.length>0){
        for(let i=0;i<data.studyBreaks.length;i++){
          var days = Date.parse(data.studyBreaks[i].breakEndDate) - Date.parse(data.studyBreaks[i].breakStartDate);
          studyBreakDays = studyBreakDays + Math.round((days/(1000*60*60*24)));
        }
    }
    var ifBreakExceedsMaxPercentage = (studyBreakDays > (studyDays * 0.1));
    var ifBreakExceedMaxDays = (studyBreakDays > 21);

    if(ifBreakExceedsMaxPercentage){
        errorMessage = "Your break(s) can’t exceed 10% of study dates.";
    }

    if(ifBreakExceedMaxDays) {
        errorMessage = errorMessage ? "Your break(s) can’t exceed 10% of study dates and exceed 21 days." :"Your break(s) can’t exceed 21 days.";
    }
    valid = !errorMessage ? true : "This program is ineligible for StudentAid BC funding. " + errorMessage;

}
else{
    valid=(row.breakStartDate) ? (row.breakStartDate<row.breakEndDate) ? true : 'End Date must be after Break Start Date' : 'Please provide Break Start date';
}
