components.calculateValue:
/**
 * Validates if the date provided is before six weeks from today.
 *
 * The same validation exists on all the student application
 * forms and also on the server side.
 *
 * When there is an update to this logic make sure to update in all the above.
 */

function isGivenDateBeforeSixWeeksFromToday(givenDate) {
  const sixWeeksFromToday = new Date(Date.now() + 42*24*60*60*1000);
  return moment
    .utc(givenDate)
    .isBefore(moment.utc(sixWeeksFromToday).format("YYYY-MM-DD"), "day");
}

// If a study period is selected, validate its study end date to be before six weeks from today.
value =
  !!data.selectedOfferingEndDate &&
  isGivenDateBeforeSixWeeksFromToday(data.selectedOfferingEndDate);
