// To calculate the time difference of studystartDate and programYearStartDate
const first_difference_In_Time =
  new Date(data.studystartDate).getTime() -
  new Date(data.programYearStartDate).getTime();
// To calculate the time difference of programYearEndDate and studystartDate
const last_difference_In_Time =
  new Date(data.programYearEndDate).getTime() -
  new Date(data.studystartDate).getTime();

// To calculate the no. of days between two dates
const first_difference_In_Days = first_difference_In_Time / (1000 * 3600 * 24);

// To calculate the no. of days between two dates
const last_difference_In_Days = last_difference_In_Time / (1000 * 3600 * 24);

// studystartDate should be equal or in between program year start and end date
valid =
  first_difference_In_Days >= 0 && last_difference_In_Days >= 0
    ? true
    : "Study start date should be within the program year";

// To calculate the time difference of two dates
const Difference_In_Time =
  new Date(data.studyendDate).getTime() -
  new Date(data.studystartDate).getTime();

// To calculate the no. of days between two dates
const Difference_In_Days = Difference_In_Time / (1000 * 3600 * 24);
valid = data.studystartDate
  ? Difference_In_Days <= data.studyPeriodMaxDays
    ? true
    : `Invalid Study Period, Dates must not be greater than ${data.studyPeriodMaxDays} days`
  : "Please provide Study Start date";

components.calculateValue:
const isStudentIndependant =
  data.hasDependents === "yes" ||
  ["married", "other", "marriedUnable"].includes(data.relationshipStatus) ||
  data.outOfHighSchoolFor4Years === "yes" ||
  data.fulltimelabourForce === "yes" ||
  data.parentsDeceased === "yes" ||
  data.estranged === "yes" ||
  data.custodyOfChildWelfare === "yes";
// If the student is not independant, dependant status is calculated based on personal information answer inputs.
if (!isStudentIndependant) {
  const isPersonalInfoAnswered =
    !!data.hasDependents &&
    !!data.relationshipStatus &&
    !!data.outOfHighSchoolFor4Years &&
    !!data.fulltimelabourForce &&
    !!data.parentsDeceased &&
    !!data.estranged &&
    !!data.youthInCare &&
    (data.youthInCare === "no" || !!data.custodyOfChildWelfare);
  // If the answers to required personal information inputs are not provided, set the dependant status to null.
  // Otherwise, dependant status is calculated based on personal information answers.
  const calculatedStatusFromPersonalInfo = isPersonalInfoAnswered
    ? "dependant"
    : null;
  instance.setValue(calculatedStatusFromPersonalInfo);
} else {
  instance.setValue("independant");
}

const hasReceivedInputs = !!data.isYourPartnerEligibleForBCSCAccount && !!data.hasCurrentYearPartnerIncome;
const isPartnerEligibleToReport = (data.isYourPartnerEligibleForBCSCAccount === "yes" && data.hasCurrentYearPartnerIncome === "no");
value = hasReceivedInputs ? isPartnerEligibleToReport : null;
