const first_difference_In_Time =
  new Date(data.studystartDate).getTime() -
  new Date(data.programYearStartDate).getTime();
// To calculate the time difference of programYearEndDate and studystartDate
const last_difference_In_Time =
  new Date(data.programYearEndDate).getTime() -
  new Date(data.studystartDate).getTime();

// To calculate the no. of days between two dates
const first_difference_In_Days = first_difference_In_Time / (1000 * 3600 * 24);

// To calculate the no. of days between two dates
const last_difference_In_Days = last_difference_In_Time / (1000 * 3600 * 24);

// studystartDate should be equal or in between program year start and end date
valid =
  first_difference_In_Days >= 0 && last_difference_In_Days >= 0
    ? true
    : "Study start date should be within the program year";

