components.calculateValue:
if (
  data.hasDependents === "no" &&
  data.relationshipStatus === "single" &&
  data.outOfHighSchoolFor4Years === "no" &&
  data.howWillYouBeAttendingTheProgram === "Full Time" &&
  data.custodyOfChildWelfare !== "yes" &&
  data.fulltimelabourForce ==="no"
) {
  instance.setValue(true);
} else {
  instance.setValue(false);
}

/**
 * Part-time is always considered "independant".
 * Full-time student will be considered "independant" if
 * parent information does not need to be entered after 
 * all the questions in the personal information are answered or 
 * if parent information needs to be entered and student
 * 	- has both the parents passed away or; 
 * 	- is estranged from both of parents. 
 * Otherwise, student is considered "dependant".
 */
 
const personalInfoAnswered = !!data.hasDependents &&
  !!data.relationshipStatus &&
  !!data.outOfHighSchoolFor4Years &&
  !!data.fulltimelabourForce &&
  (["no","preferNotToAnswer"].includes(data.youthInCare) || !!data.custodyOfChildWelfare);

if (data.howWillYouBeAttendingTheProgram === "Part Time") {
	instance.setValue("independant");
} else if (personalInfoAnswered && !data.showParentInformation) { 
	instance.setValue("independant");
} else if (data.showParentInformation && 
	["bothOfMyHaveParentsHavePassedAwayAndIHaveNoLegalGuardian","estranged"].includes(data.parentbcResident)) {
	instance.setValue("independant");
} else {
	instance.setValue("dependant");
}

