validate.custom:
// Common SIN validation algorithm (Luhn algorithm).
valid = false;
if (input) {
  input = input.replace(/\s/g, "");
  if (input.length === 9) {
    var checksum = 0;
    for (var i = 0; i < input.length; i++) {
      const currentDigit = parseInt(input.charAt(i));
      if ((i + 1) % 2 === 0) {
        const digitTimes2 = currentDigit * 2;
        checksum += digitTimes2 < 10 ? digitTimes2 : digitTimes2 - 9;
      } else {
        checksum += parseInt(input.charAt(i));
      }
    } 
    if (checksum % 10 === 0) {
      valid = true;
    }
  }
}
